import React, { useEffect, useState } from "react";
import axios from "./axios";
import request from "./request";
import "./Banner.css";

function Banner() {
    const [movie, setMovie] = useState([]);

    useEffect(() => {
        async function fetchData() {
            const requesta = await axios.get(request.fetchNetflixOriginals);
            setMovie(
                requesta.data.results[
                    /* selects one item randomly */
                    Math.floor(Math.random() * requesta.data.results.length - 1)
                ]
            );
            return requesta;
        }
        fetchData();
    }, []);

    return (
        <header 
            className="banner"
                style={{
                    backgroundSize: "cover",
                    backgroundImage: `url(
                        "https://image.tmdb.org/t/p/original/${movie?.backdrop_path}"
                        )`,
                    backgroundPosition: "center center",
                }}
        > 
            <div className='banner__contents'>
                <h1>
                    {movie?.title || movie?.name || movie?.original_name}
                </h1>

                <div className="banner__buttons">
                    <button className="banner__button">Play</button>
                    <button className="banner__button">My Llist</button>
                </div>

                <h1 className="banner__description">{movie?.overview}</h1>

                <div className='faded__banner'></div>
            </div>
        </header>
    )
}


export default Banner
