import React, { useEffect, useState } from "react";
import axios from "./axios";
import request from "./request";

function Banner() {
    const [movie, setMovie] = useState([]);

    useEffect(() => {
        async function fetchData() {
            const requesta = await axios.get(request.fetchNetflixOriginals);
            setMovie(
                requesta.data.results[
                    /* selects one item randomly */
                    Math.floor(Math.random() * requesta.data.results.length - 1)
                ]
            );
            return requesta;
        }
        fetchData();
    }, []);

  //  console.log(movie);

    return (
        <header 
            className="banner"
                style={{
                    backgroundSize: "cover",
                    backgroundImage: `url(
                        "https://image.tmdb.org/t/p/original/${movie?.backdrop.path}"
                        )`,
                    backgroundPosition: "center center",
                }}
        > 
            <div className='banner__contents'>
                <h1>
                    {movie?.title || movie?.name || movie?.original_name}
                </h1>
                { /* div > 2 buttons */}
                { /* description*/}
            </div>
        </header>
    )
}


export default Banner
