        // if [], run once when the row loads ad don' t run again
        async function fetchDAta() {
            const request = await axios.get(fetchUrl);
            setMovies(request.data.results);
            return request;
        }
        fetchDAta();
    }, [fetchUrl])


  return (
    <div className="row">
        <h2>{title}</h2>
        <div className="row__posters">
            {/ *several row__posters */}

            {movies.map()}
        </div>

    </div>
  )
}

